import React, { useState } from 'react'

const CreateTask = () => {

    const [note,setNote] = useState({
        title:'',
        content:''
    });

    const [hideData, isHideData] = useState(false);//to display title

    const formHandler=(e)=>{
        const {name,value} = e.target;
        setNote((preValue)=>{
            return {
                ...preValue,
                [name]:value
            }
        })
    }

    const formSubmit=(e)=>{
        e.preventDefault();
        isHideData(false);

    }

    //to display title section
    const showTitle=()=>{
        isHideData(true)
    }

  return (
    <>
        <form onSubmit={formSubmit}>
        {
            
        }
            <input type='text' value={note.title} placeholder='Title' name="title" onChange={formHandler}/>
            <p>
                <textarea onclich={showTitle} name="content" value={note.content} placeholder='Add a task..'  onChange={formHandler}>
                </textarea>
            </p>
            <input type='submit'></input>

        </form>
    </>
  )
}

export default CreateTask
