import React, { useState } from 'react'
import { GrAdd } from 'react-icons/gr';

const CreateArea = ({onAdd}) => {

    const [note,setNote] = useState({
        title:'',
        content:''
    })

    const [isExpand,setExpand] = useState(false);

    const handleChange=(e)=>{
        console.log(e);
        const {name,value} = e.target;
        console.log(name);
        console.log(value);

        setNote((preValue)=>{
            return {
                ...preValue,
                [name]:value
            }
        })
    }

    const submitButton=(e)=>{
        e.preventDefault();
        onAdd(note);
        setNote({
            title:'',
            content:''
        })
        setExpand(false);
    }

    const handleExpand=(e)=>{
        e.stopPropagation();
        setExpand(true);
    }




    return (
        <>
            <form>
            {
                isExpand && (<input value={note.title} type='text' placeholder='title' name="title" onChange={handleChange}></input>)
            }
                <p>
                    <textarea rows={isExpand?3:1} onClick={handleExpand} value={note.content} name='content' placeholder='take a note' onChange={handleChange}></textarea>    
                </p>

                <button onClick={submitButton}><GrAdd   /></button>
            </form>
        </>
    )
}

export default CreateArea
