import React, { useReducer, useState } from 'react'
import List from './List'


const Calculate = () => {

    const ACTIONS={
        ADD_TODO:'add_todo',
        DELETE_TODO:'delete_todo',
    }

    const reducer=(todo,action)=>{
        switch(action.type){
            case ACTIONS.ADD_TODO:
                return [...todo,{id:Date.now(),name:action.payload.name}]
            case ACTIONS.DELETE_TODO:
                return todo.filter(todo=>todo.id!==action)
        }
    }

   const [todo,dispatch] = useReducer(reducer,[]);

    const [name,setName] = useState('');
    const fetchVal=(e)=>{
        const {name,value} = e.target;
        setName(value);
    }

    const formHandler=(e)=>{
        e.preventDefault();
        dispatch({type:ACTIONS.ADD_TODO, payload:{name:name}}) 
        setName('');
    }

    //console print
    console.log(todo);

    return (
    <>
        <form onSubmit={formHandler}>
            <input type='text' name="todo" value={name} onChange={fetchVal}/>
        </form>

        {
            todo.map((todo)=>{
                return <List key={todo.id} todo={todo} dispatch={dispatch} ACTION={ACTIONS}/>
            })
        }
    </>
    )
}

export default Calculate
