import React, { useContext, useState } from 'react'
import { TaskData } from './Context';

const CreateTask = () => {
    const {task, setTask} = useContext(TaskData);
    
    const [note,setNote] = useState({
        title:'',
        content:''
    });

    const [hideData, isHideData] = useState(false);//to toggle title

    const formHandler=(e)=>{
        const {name,value} = e.target;
        setNote((preValue)=>{
            return {
                ...preValue,
                [name]:value
            }
        })
    }

    const formSubmit=(e)=>{
        e.preventDefault();
        if(note.title===''){
            return false;
        }else{
            setTask((preValue)=>{
                return [...preValue,note]
            })
            setNote({
                title:'',
                content:''
            })
            
        }


    }

    //to display title section
    const showTitle=()=>{
        isHideData(true)
    }

  return (
    <>
        <form onSubmit={formSubmit}>
        {
            hideData && (<input type='text' value={note.title} placeholder='Title' name="title" onChange={formHandler}/>)
        }
            <p>
                <textarea onClick={showTitle} name="content" value={note.content} placeholder='Add a task..'  onChange={formHandler}>
                </textarea>
            </p>
            <input type='submit'/>
        </form>
    </>
  )
}

export default CreateTask
