import { useContext, useState } from "react";
import { CartData } from "./components/Context";
import Count from "./components/Count";
import CreateArea from "./components/CreateArea";
import Header from "./components/Header";
import Note from "./components/Note";


function App() {
  const {notes, setNotes} = useContext(CartData);

  const addNote=(note)=>{
    setNotes((preVal)=>{
      return [...preVal,note];  
    })
  }

  const deleteNotes=(id)=>{
    setNotes((preVal)=>{
      return [...preVal.filter((note,index)=>index!==id)]
    })
  }
  return (
      <>
        <Header/>
        <Count count={notes.length===0? 'Empty':notes.length}/>
        <Count/>
        <CreateArea onAdd={addNote}/>
        {
          notes.map((val,idx)=>{
            return (
              <Note key={idx} id={idx} title={val.title} content={val.content} onDelete={deleteNotes}/>
            )
          })
        }
      </>
    );
}

export default App;
