import React, { createContext, useCallback, useEffect, useState } from 'react'

export const TaskData = createContext();

export const Context = ({children}) => {
    const [task, setTask] = useState([]);
    const [isLoading,setIsLoading]=useState(false);

    const fetchTaskHandler=useCallback(()=>{
        setIsLoading(true);
        fetch('https://kapse-task-default-rtdb.firebaseio.com/task.json')
        .then((response)=>{
            return response.json()})
        .then((data)=>{
            setIsLoading(false);
            for(let key in data){
                setTask.push(data[key])
                console.log(key)
            }})
        .catch((err)=>{
            setIsLoading(false);
        })
    },[])

    useEffect(()=>{
        fetchTaskHandler();
    },[fetchTaskHandler])


    return (
        <TaskData.Provider value={{task, setTask,isLoading,setIsLoading}}>
            {children}
        </TaskData.Provider>
    )
}

export default Context
