import React from 'react'
import { AiFillDelete } from 'react-icons/ai';
import { CartData } from './Context';

const Note = ({title,content,id}) => {
    const {setNotes} = useContext(CartData);

    const onDelete=(id)=>{
        setNotes((preVal)=>{
            return [...preVal.filter((val,index)=>index!==id)]
          })
    }

  return (
    <div className='note'>
        <h1>{title}</h1>
        <p>{content}</p>
        <button onClick={()=>onDelete(id)}><AiFillDelete/></button>
    </div>
  )
}

export default Note
