import React, { createContext, useEffect, useState } from 'react'

export const TaskData = createContext();

export const Context = ({children}) => {
    const [task, setTask] = useState([]);
    const [isLoading,setIsLoading]=useState(false);

    //  const fetchTaskHandler=()=>{
    //      const response = await  fetch('https://kapse-task-default-rtdb.firebaseio.com/');
    //      const data = await response.json
    //      const transformedData = data.toString();
    //  }   

    const fetchTaskHandler=()=>{
        setIsLoading(true);
        fetch('https://kapse-task-default-rtdb.firebaseio.com/data.json').then((response)=>{
            return response.json
        }).then((data)=>{
            //transform
            setIsLoading(false);
        }).catch((err)=>{
            setIsLoading
        })
    }

    useEffect(()=>{
        fetchTaskHandler();
    },[])


    return (
        <TaskData.Provider value={{task, setTask,isLoading,setIsLoading}}>
            {children}
        </TaskData.Provider>
    )
}

export default Context
